mapper.update:
update tbl_chat_room 
set status = '0', updatedate = current_timestamp()
where chnum = #{chnum}

update tbl_chat_room 
set roomNick=#{roomNick}, maxNum=#{maxNum}, updatedate = current_timestamp()
where chnum = #{chnum}

update tbl_chat_room 
set currentNum = currentNum + 1 
where chnum = #{chnum};

update tbl_chat_room 
set currentNum = currentNum - 1 
where chnum = #{chnum};

update tbl_chat_room 
set userid = concat(userid,'|', #{userid})
where chnum = #{chnum}

mapper.select:
select nickname
from tbl_user 
where id in (select id from tbl_chat_validate where chnum = #{chnum} and (validate = 2 or validate = 4));
